import styles from './NovoPedido.module.css'

import { uploads } from '../../utils/config'

//components
import Message from '../../components/message/Message'
import { Link } from 'react-router-dom'

//hooks
import { useState, useEffect, useRef } from 'react'
import { useSelector, useDispatch} from 'react-redux'
import { useParams } from 'react-router-dom'

//redux
import {profile, resetMessage, updateProfile} from '../../slices/userSlice'
import { publishPedido } from '../../slices/pedidoSlice'

const NovoPedido = () => {

  // load user data
  const dispatch = useDispatch()
  const {user, message, loading} = useSelector((state) => state.user)
  
  useEffect(() => {
    dispatch(profile())
  }, [dispatch])

  const {
    pedidos, 
    loading: loadingPedido, 
    message: messagePedido, 
    error: errorPedido
  } = useSelector((state) => state.pedido)

  //states form
  const [titulo, setTitulo] = useState("")
  const [local, setLocal] = useState("")
  const [sangue, setSangue] = useState([])
  const [desc, setDesc] = useState("")

  //states pedido user
  const [username, setUserName] = useState("")
  const [image, setImage] = useState("")
  const [userId, setUserId] = useState("")

  //aux
  const [tipos, setTipos] = useState("")

  //pedido
  const newPhotoForm = useRef()

  const handleSubmit = (e) => {
    e.preventDefault()

    const pedidoData = {
      titulo,
      username,
      image,
      userId,
      local,
      tipos,
      desc
    }

    if(tipos){
      setTipos(tipos.split(","))
    }

    if(user){
      pedidoData.username = user.nome
      pedidoData.image = user.profileImage
      pedidoData.userId = user.userId
    }

  }

  const addType = (type) => {
    //limpa seleção
    if(type===''){
      setTipos(type)
    }
    //não deixa duas vezes o mesmo tipo
    if(tipos.includes(type)){
      return
    }
    if(tipos === ""){
      setTipos(tipos + `${type}`)
      return
    }
    setTipos(tipos + `, ${type}`)
  }

  return (
    <div className={styles.container}>
      <div className={styles.new_pedido} ref={newPhotoForm}>
        <h2>Preencha o formulário e poste o seu pedido</h2>
        <form onSubmit={handleSubmit} className={styles.form}>
          <label>
            <span>Título:</span>
            <input
              type="text"
              placeholder='Digite um título'
              onChange={(e) => setTitulo(e.target.value)}
              value={titulo}
            />
          </label>
          <label>
            <span>Local da doação:</span>
            <input
              type="text"
              placeholder='Digite o local de doação'
              onChange={(e) => setLocal(e.target.value)}
              value={local}
            />
          </label>
          <label>
            <span>Tipos sanguineos:</span>
            <input 
              type="text" 
              disabled
              value={tipos} 
              placeholder="Selecione as opçoes abaixo"
            />
            <div className={styles.actions}>
              <input type="button" onClick={() => addType('A+')} value="A+"/>
              <input type="button" onClick={() => addType('A-')} value="A-"/>
              <input type="button" onClick={() => addType('B+')} value="B+"/>
              <input type="button" onClick={() => addType('B-')} value="B-"/>
              <input type="button" onClick={() => addType('AB+')} value="AB+"/>
              <input type="button" onClick={() => addType('AB-')} value="AB-"/>
              <input type="button" onClick={() => addType('O+')} value="O+"/>
              <input type="button" onClick={() => addType('O-')} value="O-"/>
              <input type="button" onClick={() => addType('')} value="Limpar seleção"/>
            </div>
          </label>
          <label>
            <span>Descrição:</span>
            <textarea
              cols="30"
              rows="10"
              onChange={(e) => setDesc(e.target.value)}
              value={desc}
            ></textarea>
          </label>
          <button onClick={handleSubmit} className="btn_global">Realizar pedido</button>
        </form>
      </div>
    </div>
  )
}

export default NovoPedido
