import {api} from '../utils/config'

const requestPassword = async(email) => {
    const config = {
        method: "POST",
        body: JSON.stringify(email),
        headers: {
            "X-Powered-By": "Express",
            "Access-Control-Allow-Origin": "http://localhost:3000",
            "Vary": "Origin",
            "Access-Control-Allow-Credentials": true,
            "Content-Type": "application/json; charset=utf-8",
            "Content-Length": 30,
            "ETag": 'W/"1e-ercbpbvgZl3gw6+Oh6868bvbv/Y"',
            "Date": Date.now(),
            "Connection": "keep-alive",
            "Keep-Alive": "timeout=5"
        }
    }
    try{
        const res = await fetch(api + "/users/request", config)
            .then((res) => res.json())
            .catch((err) => err)
        return res
    } catch(error){
        console.log(error)
    }
}

const setPassword = async(email, token, senha) => {
    const data = {
        email,
        token,
        senha
    }
    const config = {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
            "Content-Type": "application/json"
        }
    }
    try{
        const res = await fetch(api + "/users/reseta", config)
            .then((res) => res.json())
            .catch((err) => err)
        return res
    } catch(error){
        console.log(error)
    }
}

const passwordService = {
    requestPassword,
    setPassword
}

export default passwordService
