import styles from './Pedidos.module.css'

//components
import CardPedido from '../../components/cardpedido/CardPedido'
import MenuPerfil from '../../components/menu/MenuPerfil'
import { BsClipboardPlus } from "react-icons/bs";
import FormSearch from '../../components/formsearch/FormSearch';

//hooks
import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom' 
import { useAuth } from '../../hooks/useAuth'

//redux
import { getAllPedidos } from '../../slices/pedidoSlice';

const Pedidos = () => {

  const {auth} = useAuth()

  const dispatch = useDispatch()
  const {pedidos, loading} = useSelector((state) => state.pedido)

  //load all pedidos
  useEffect(() => {
    dispatch(getAllPedidos())
  }, [dispatch])

  if(loading){
    return <p>Carregando...</p>
  }

  const handleSearch = () => {
    alert('oiiiii')
  }

  return (
    <>
    {!auth && (
      <>
        <header className={styles.header}>
          <FormSearch
            handle={handleSearch}
          />
          <Link to='/login' className='btn_global'><BsClipboardPlus/> Novo pedido</Link>
        </header>
      </>
    )}
    {auth && (
      <>
        <header className={styles.header}>
          <MenuPerfil />
          <div className={styles.teste}>
            <FormSearch />
            <div className={styles.types}>
              <button>A+</button>
              <button>A-</button>
              <button>B+</button>
              <button>B-</button>
              <button>AB+</button>
              <button>AB-</button>
              <button>O+</button>
              <button>O-</button>
            </div>
          </div>
          <Link to='/novopedido' className='btn_global'><BsClipboardPlus/> Novo pedido</Link>
        </header>
      </>
    )}
    <div className={styles.container}>
      <div className={styles.pedidos}>
        {pedidos && 
          pedidos.map((pedido) => (
            <CardPedido
              key={pedido._id}
              url={pedido.image}
              username={pedido.username}
              local={pedido.local}
              tipos={pedido.tipos}
              desc={pedido.desc}
            />
          ))}
      </div>
    </div>
  </>
  )
}
export default Pedidos
