import styles from './EditProfile.module.css'

import { uploads } from '../../utils/config'

import { useState, useEffect } from 'react'
import { useSelector, useDispatch} from 'react-redux'

import {profile, resetMessage, updateProfile} from '../../slices/userSlice'

import Message from '../../components/message/Message'

const EditProfile = () => {

    const dispatch = useDispatch()
    const {user, message, loading} = useSelector((state) => state.user)

    //states
    const [nome, setNome] = useState("")
    const [sobrenome, setSobrenome] = useState("")
    const [profileImage, setProfileImage] = useState("")
    const [tipoSangue, setTiposangue] = useState("")
    const [celular, setCelular] = useState("")
    const [local, setLocal] = useState("")
    const [senhaNova, setSenhaNova] = useState("")
    const [confirmaSenha, setConfirmaSenha] = useState("")

    const [previewImage, setPreviewImage] = useState("")

    // load user data
    useEffect(() => {
        dispatch(profile())
    }, [dispatch])

    //fill form with user data
    useEffect(() => {
        if(user){
            setNome(user.nome)
            setSobrenome(user.sobrenome)
            setTiposangue(user.tipoSangue)
            setCelular(user.celular)
            setLocal(user.local)
        }
    }, [user])

    const [trocarSenha, setTrocarSenha] = useState(false)

    const tipos = [
        "A+",
        "A-",
        "B+",
        "B-",
        "AB+",
        "AB-",
        "O+",
        "O-"
    ]

    const handleSubmit = async(e) => {
        e.preventDefault()
        window.alert('Entrou')
        /*const userData = {
            nome,
        }
        if(profileImage){
            userData.profileImage = profileImage
        }
        if(sobrenome){
            userData.sobrenome = sobrenome
        }
        if(tipoSangue){
            userData.tipoSangue = tipoSangue
        }
        if(celular){
            userData.celular = celular
        }
        if(local){
            userData.local = local
        }

        if(senhaNova){
            if(senhaNova === confirmaSenha){
                userData.senha = senhaNova
            }
        }
        dispatch(updateProfile(userData))

        setTimeout(() => {
            dispatch(resetMessage())
        }, 2000)*/
    }
    const handleFile = (e) => {
        // image preview
        const image = e.target.files[0]
        setPreviewImage(image)

        //update
        setProfileImage(image)
    }

    const toggleTrocarSenha = () => {
        setTrocarSenha(!trocarSenha)
    }

  return (
    <div className={styles.container}>
        <div className={styles.header}>
            <h2>Edite seu perfil</h2>
            <p className={styles.subtitle}>Adicione uma foto e altere o que quiser!</p>
        </div>
        <div className={styles.img}>
            {(user.profileImage || previewImage) && (
                <img
                    className={styles.image}
                    src={
                        previewImage ? URL.createObjectURL(previewImage) : `${uploads}/users/${user.profileImage}`
                    }
                    alt={user.nome}
                />
            )}
        </div>
            <div className={styles.content_form}>
                <form onSubmit={handleSubmit} className={styles.form}>
                    <label>
                        <span>Nome:</span>
                        <input
                            type="text"
                            placeholder='Digite seu nome'
                            onChange={(e) => setNome(e.target.value)}
                            value={nome || ""}
                        />
                    </label>
                    <label>
                        <span>Sobrenome:</span>
                        <input
                            type="text"
                            placeholder='Digite seu sobrenome'
                            onChange={(e) => setSobrenome(e.target.value)}
                            value={sobrenome || ""}
                        />
                    </label>
                    <div className={styles.upload}>
                        <label>
                            <span>Imagem do perfil:</span>
                            <input
                                type="file"
                                onChange={handleFile}
                            />
                        </label>
                    </div>
                    <div className={styles.tipo}>
                        <label>
                            <span>Tipo sanguíneo:</span>
                            <select
                                name="sangue"
                                text="Tipo sanguineo"
                                onChange={(e) => setTiposangue(e.target.value)}
                                value={tipoSangue || ""}
                            >
                                <option disabled>Selecione uma opção</option>
                                    {
                                        tipos && tipos.map(
                                            (tipo) => (<option key={tipos.indexOf(tipo)} value={tipo}>{tipo}</option>)
                                        )
                                    }
                            </select>
                        </label>
                    </div>
                   <label>
                        <span>Celular:</span>
                        <input
                            type="text"
                            placeholder='Digite seu celular'
                            onChange={(e) => setCelular(e.target.value)}
                            value={celular || ""}
                        />
                   </label>
                   <label>
                        <span>Localização:</span>
                        <input
                            type="text"
                            placeholder='Digite sua localização'
                            onChange={(e) => setLocal(e.target.value)}
                            value={local || ""}
                        />
                   </label>
                   {trocarSenha && (
                        <>
                            <label>
                                <span>Senha atual:</span>
                                <input
                                    type="text"
                                    placeholder="Nova senha"
                                    onChange={(e) => setSenhaNova(e.target.value)}
                                    value={senhaNova || ""}
                                />
                            </label>
                            <input
                                type="text"
                                placeholder="Confirmar senha"
                                onChange={(e) => setConfirmaSenha(e.target.value)}
                                value={confirmaSenha || ""}
                            />
                        </>
                    )}
                    <div className={styles.actions}>
                        <button onClick={toggleTrocarSenha} className={styles.btntrade}>{!trocarSenha ? "Trocar senha" : "Fechar"}</button>
                        {!loading && <button onClick={() => handleSubmit} className='btn_global'>Atualizar</button>}
                        {loading && <button className='btn_global' disabled="disabled">Aguarde...</button>}
                    </div>
                    {/*<button onClick={() => handleSubmit} className='btn_global'>Salvar</button>*/}
                    {message && <Message msg={message} type="success"/>}
                </form>
            </div>
    </div>
  )
}

export default EditProfile
