import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import pedidoService from '../services/pedidoService'

const initialState ={
    pedidos: [],
    pedido: {},
    error: false,
    success: false,
    loading: false,
    message: null
}

// publish pedido
export const publishPedido = createAsyncThunk(
    "pedido/publish",
    async(pedido, thunkAPI) => {
        const token = thunkAPI.getState().auth.user.token

        const data = await pedidoService.publishPedido(pedido, token)
        //check errors
        if(data.errors){
            return thunkAPI.rejectWithValue(data.errors[0])
        }
        return data
    }
)

//get all pedidos
export const getAllPedidos = createAsyncThunk(
    "pedido/getall",
    async(thunkAPI) => {
        const token = thunkAPI.getState().auth.user.token
        const data = await pedidoService.getAllPedidos(token)
        return data
    }
)

export const pedidoSlice = createSlice({
    name: "pedido",
    initialState,
    reducers: {
        resetMessage: (state) => {
            state.message = null
        }
    },
    extraReducers: (builder) => {
        builder
            .addCase(publishPedido.pending, (state) => {
                state.loading = true
                state.error = false
            })
            .addCase(publishPedido.fulfilled, (state, action) => {
                state.loading = false
                state.success = true
                state.error = null
                state.pedido = action.payload
                state.pedidos.unshift(state.photo)
                state.message = "Pedido publicado com sucesso!!!"
            })
            .addCase(publishPedido.rejected, (state, action) => {
                state.loading = false
                state.error = action.payload
                state.pedido = {}
            })
            .addCase(getAllPedidos.pending, (state) => {
                state.loading = true
                state.error = false
            })
            .addCase(getAllPedidos.fulfilled, (state, action) => {
                state.loading = false
                state.success = true
                state.error = null
                state.pedidos = action.payload
            })
    }
})

export const {resetMessage} = pedidoSlice.actions
export default pedidoSlice.reducer
