import styles from './NovoPedido.module.css'

import { uploads } from '../../utils/config'

//components
import Message from '../../components/message/Message'
import { Link } from 'react-router-dom'

//hooks
import { useState, useEffect, useRef } from 'react'
import { useSelector, useDispatch} from 'react-redux'
import { useParams } from 'react-router-dom'

//redux


const NovoPedido = () => {
  const [tipos, setTipos] = useState("")

  //pedido
  const newPhotoForm = useRef()

  const handleSubmit = () => {

  }
  const addType = (type) => {
    //limpa seleção
    if(type===''){
      setTipos(type)
    }
    //não deixa duas vezes o mesmo tipo
    if(tipos.includes(type)){
      return
    }
    if(tipos === ""){
      setTipos(tipos + `${type}`)
      return
    }
    setTipos(tipos + `, ${type}`)
  }

  return (
    <div className={styles.container}>
      <div className={styles.new_pedido} ref={newPhotoForm}>
        <h3>Preencha o formulário e poste o seu pedido</h3>
        <form onSubmit={handleSubmit} className={styles.form}>
          <label>
            <span>Título:</span>
            <input type="text" placeholder='Insira um título'/>
          </label>
          <label>
            <span>Local da doação:</span>
            <input type="text" placeholder='Insira o local de doação'/>
          </label>
          <label>
            <span>Tipos sanguineos:</span>
            <input type="text" disabled value={tipos}/>
            <div className={styles.actions}>
              <input type="button" onClick={() => addType('A+')} value="A+"/>
              <input type="button" onClick={() => addType('A-')} value="A-"/>
              <input type="button" onClick={() => addType('B+')} value="B+"/>
              <input type="button" onClick={() => addType('B-')} value="B-"/>
              <input type="button" onClick={() => addType('AB+')} value="AB+"/>
              <input type="button" onClick={() => addType('AB-')} value="AB-"/>
              <input type="button" onClick={() => addType('O+')} value="O+"/>
              <input type="button" onClick={() => addType('O-')} value="O-"/>
              <input type="button" onClick={() => addType('')} value="Limpar seleção"/>
            </div>
          </label>
          <label>
            <span>Descrição:</span>
            <textarea cols="20" rows="10"></textarea>
          </label>
          <input type="submit" value='Realizar pedido'/>
        </form>
      </div>
    </div>
  )
}

export default NovoPedido
