import {api} from '../utils/config'

const requestPassword = async(email) => {

    const data = {
        "email": email
    }

    const config = {
        method: "POST",
        body: data,
    }

    console.log(config)
    try{
        const res = await fetch(api + "/users/request", config)
            .then((res) => res.json())
            .catch((err) => err)
        return res
    } catch(error){
        console.log(error)
    }
}

const setPassword = async(email, token, senha) => {
    const data = {
        email,
        token,
        senha
    }
    const config = {
        method: "POST",
        body: JSON.stringify(data),
    }
    try{
        const res = await fetch(api + "/users/reseta", config)
            .then((res) => res.json())
            .catch((err) => err)
        return res
    } catch(error){
        console.log(error)
    }
}

const passwordService = {
    requestPassword,
    setPassword
}

export default passwordService
