import styles from './CardPedido.module.css'
import { AiFillPhone } from "react-icons/ai";
import { useAuth } from '../../hooks/useAuth'
import { Link } from 'react-router-dom';
import { uploads } from '../../utils/config';

const CardPedido = ({url, username, local, tipos, desc}) => {

    const {auth} = useAuth()
    
    const contact = () => {
        alert("Email enviado!!!")
    }

  return (
    <div className={styles.card}>
        <div className={styles.super_info}>
            <div className={styles.user_info}>
                <img src={`${uploads}/users/${url}`} alt="perfil img" />
            </div>
                <div>
                    <p>{username}</p>
                    <p>{local}</p>
                </div>
                <p>
                {tipos && tipos.map((tipo, index) => (
                    <span key={index}>{tipo}</span>
                ))}
                </p>
        </div>
        <div className={styles.textarea}>
            <div>
                {desc}
            </div>
        </div>
        <div className={styles.btn}>
            {auth && (<button onClick={contact}><AiFillPhone/>Entrar em contato</button>)}
            {!auth && (<Link to="/login"><AiFillPhone/>Entrar em contato</Link>)}
        </div>
    </div>
  )
}

export default CardPedido
