import React, { useState } from 'react'
import './Password.css'

import Message from '../../components/message/Message'
import {useSelector, useDispatch} from 'react-redux'
import { resetMessage } from '../../slices/userSlice'
//service
import {requestNewPassword} from '../../slices/userSlice'

const RequestPassword = () => {

    const {error, loading, message} = useSelector((state) => state.user)
    const dispatch = useDispatch()

    const [email, setEmail] = useState("")

    const handleSubmit = (e) => {
        e.preventDefault()
        //errors
        dispatch(requestNewPassword(email))
    }

    setTimeout(() => {
      dispatch(resetMessage())
    }, 3000)

  return (
    <div className="request-container">
        <form onSubmit={handleSubmit}>
            <input type="email" placeholder="Digite seu E-mail" required onChange={(e) => setEmail(e.target.value)}/>
            {!loading && <button onClick={() => handleSubmit} className='btn_global'>Enviar</button>}
            {loading && <button className='btn_global' disabled="disabled">Aguarde...</button>}
            {message && <Message type='success' msg={message}/>}
        </form>
    </div>
  )
}

export default RequestPassword
