import styles from './Register.module.css'
import image from '../../assets/imgs/4-vidas.png'

//components
import Message from '../../components/message/Message'

//hooks
import { useState, useEffect } from 'react'
import { useSelector, useDispatch} from 'react-redux'

//redux
import {register, reset} from "../../slices/authSlice"

const Register = () => {

  const [nome, setNome] = useState("")
  const [sobrenome, setSobrenome] = useState("")
  const [email, setEmail] = useState("")
  const [ano, setAno] = useState("")
  const [tiposangue, setTiposangue] = useState("")
  const [celular, setCelular] = useState("")
  const [local, setLocal] = useState("")
  const [senha, setSenha] = useState("")
  const [confirmaSenha, setConfirmaSenha] = useState("")
  
  const tipos = ["A+", "A-", "B+", "B-", "AB+", "AB-", "O+", "O-"]

  const dispatch = useDispatch()

  const {loading, error} = useSelector((state) => state.auth)
  
  const handleSubmit = (e) => {
    e.preventDefault()

    const user = {
      nome,
      sobrenome,
      email,
      ano,
      tiposangue,
      celular,
      local,
      senha,
      confirmaSenha
    }
    console.log(user)
    dispatch(register(user))
  }

  useEffect(() => {
    dispatch(reset)
  }, [dispatch])

  console.log(loading)
  console.log(error)


  return (
    <>
    <div className={styles.container}>
      <div>
        <h2>Cadastre-se</h2>
        <form onSubmit={handleSubmit} className={styles.form}>
            <input
              type="text"
              name='nome'
              placeholder='Nome'
              required  
              onChange={(e) => setNome(e.target.value)}
              value={nome}         
            />
            <input
              type="text"
              name='sobrenome'
              placeholder='Sobrenome'
              required    
              onChange={(e) => setSobrenome(e.target.value)}
              value={sobrenome}        
            />
            <input
              className={styles.email}
              type="email"
              name='email'
              placeholder='E-mail'
              required   
              onChange={(e) => setEmail(e.target.value)}
              value={email}         
            />
            <input
              type="text"
              name="ano"
              placeholder="Ano de nascimento"
              required   
              onChange={(e) => setAno(e.target.value)}
              value={ano}         
            />
            {/*CONSUMO DE API PARA TESTE*/}
            <select
              name="sangue"
              text="Tipo sanguineo"
              className={styles.select}
              onChange={(e) => setTiposangue(e.target.value)}
            >
              <option disabled>Selecione uma opção</option>
              {tipos && tipos.map((tipo) => (
                <option key={tipos.indexOf(tipo)}>{tipo}</option>
              ))}
            </select>
            <input
            className={styles.celular}
              type="tel"
              name="phone"
              placeholder="Celular - ex: (xx) 9xxxx-xxxx"
              onChange={(e) => setCelular(e.target.value)}
              value={celular} 
            />
            <input
            className={styles.local}
              type="text"
              name="local"
              placeholder="Localização - ex: Porto Alegre, RS"
              onChange={(e) => setLocal(e.target.value)}
              value={local} 
            />
            <input
              type="password"
              name="senha"
              placeholder="Digite uma senha"
              onChange={(e) => setSenha(e.target.value)}
              value={senha} 
            />
            <input
              type="password"
              name="confirmasenha"
              placeholder="Confirme sua senha"
              onChange={(e) => setConfirmaSenha(e.target.value)}
              value={confirmaSenha} 
            />
            {!loading && <button onClick={() => handleSubmit} className='btn_global'>Cadastrar</button>}
        </form>
      </div>
      <div className={styles.img}>
        <img src={image} alt="4 vidas" />
      </div>
    </div>
    </>
  )
}

export default Register
