import React, { useEffect, useState } from 'react'
import styles from './EditPedido.module.css'

//hooks
import { useAuth } from '../../hooks/useAuth'

//components
import MenuPerfil from '../../components/menu/MenuPerfil'


//
import { useDispatch, useSelector } from 'react-redux'
//redux
import { getPedidoById, updatePedido, resetMessage } from '../../slices/pedidoSlice'
import { useParams } from 'react-router-dom'

const EditPedido = () => {

  const {id} = useParams()
  const {auth} = useAuth()
  const dispatch = useDispatch()
  const {pedido, message, error} = useSelector((state) => state.pedido)

  useEffect(() => {
    dispatch(getPedidoById(id))
  }, [dispatch, id])

  const [title, setTitle] = useState(pedido.titulo)
  const [local, setLocal] = useState(pedido.local)
  const [tipos, setTipos] = useState(pedido.tipos)
  const [desc, setDesc] = useState(pedido.desc)

  console.log(tipos.toLocaleString())

  setTimeout(() => {
    dispatch(resetMessage(message))
  }, 2000)

  const addType = (type) => {
    //limpa seleção
    if(type===''){
      setTipos(type)
    }
    //não deixa duas vezes o mesmo tipo
    if(tipos.includes(type)){
      return
    }
    if(tipos === ""){
      setTipos(tipos + `${type}`)
      return
    }
    setTipos(tipos + `, ${type}`)
  }
  
  const handleSubmit = (e) => {
    e.preventDefault()
    console.log('entrou!!!')
  }
  return (
    <div className={styles.container}>
       {auth && (
          <>
            <MenuPerfil />
          </>
        )}
      <div className={styles.formEditPedido}>
        <h2>Edite aquilo que desejar</h2>
        <form onSubmit={handleSubmit}>
          <label>
            <span>Título:</span>
            <input 
              type="text"
              placeholder="Digite um título"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </label>
          <label>
            <span>Local:</span>
            <input 
              type="text"
              placeholder="Digite um local"
              value={local}
              onChange={(e) => setLocal(e.target.value)}
            />
          </label>
          {/*<label>
            <span>Tipos sanguineos:</span>
            <input 
              type="text" 
              disabled
              required
              value={tipos.toLocaleString()} 
              placeholder="Selecione as opçoes abaixo"
            />
            <div className={styles.actions}>
              <input type="button" onClick={() => addType('A+')} value="A+"/>
              <input type="button" onClick={() => addType('A-')} value="A-"/>
              <input type="button" onClick={() => addType('B+')} value="B+"/>
              <input type="button" onClick={() => addType('B-')} value="B-"/>
              <input type="button" onClick={() => addType('AB+')} value="AB+"/>
              <input type="button" onClick={() => addType('AB-')} value="AB-"/>
              <input type="button" onClick={() => addType('O+')} value="O+"/>
              <input type="button" onClick={() => addType('O-')} value="O-"/>
              <input type="button" onClick={() => addType('')} value="Limpar seleção"/>
            </div>
          </label>*/}
        </form>
      </div>
    </div>
  )
}

export default EditPedido
