import React, { useEffect, useState } from 'react'
import styles from './EditPedido.module.css'

//hooks
import { useAuth } from '../../hooks/useAuth'

//components
import MenuPerfil from '../../components/menu/MenuPerfil'


//
import { useDispatch, useSelector } from 'react-redux'
//redux
import { getPedidoById, updatePedido, resetMessage } from '../../slices/pedidoSlice'
import { useParams } from 'react-router-dom'

const EditPedido = () => {

  const {id} = useParams()
  const {auth} = useAuth()
  const dispatch = useDispatch()
  const {pedido, message, error} = useSelector((state) => state.pedido)

  useEffect(() => {
    dispatch(getPedidoById(id))
  }, [dispatch, id])

  const [title, setTitle] = useState()
  const [local, setLocal] = useState()
  const [tipos, setTipos] = useState()
  const [types, setTypes] = useState()
  const [desc, setDesc] = useState()

  useEffect(() => {
    setTitle(pedido.titulo)
    setLocal(pedido.local)
    setTipos(pedido.tipos)
    setDesc(pedido.desc)

    if(tipos){
      setTypes(tipos.join(','))
    }
  }, [pedido, tipos])

  setTimeout(() => {
    dispatch(resetMessage(message))
  }, 2000)

  const addType = (type) => {
    //limpa seleção
    if(type===''){
      setTypes(type)
    }
    //não deixa duas vezes o mesmo tipo
    if(types.includes(type)){
      return
    }
    if(types === ""){
      setTypes(types + `${type}`)
      return
    }
    setTypes(types + `, ${type}`)
  }
  
  const handleSubmit = (e) => {
    e.preventDefault()
    console.log('entrou!!!')
  }
  return (
    <div className={styles.container}>
       {auth && (
          <>
            <MenuPerfil />
          </>
        )}
      <div className={styles.formEditPedido}>
        <h2>Edite aquilo que desejar</h2>
        <form onSubmit={handleSubmit}>
          
        </form>
      </div>
    </div>
  )
}

export default EditPedido
