import React, { useState } from 'react'
import './Password.css'

import Message from '../../components/message/Message'
import {useDispatch} from 'react-redux'
import { requestPassword, resetMessage } from '../../slices/passwordSlice'

const RequestPassword = () => {

  const dispatch = useDispatch()

  const [email, setEmail] = useState("")

  const handleSubmit = (e) => {
    e.preventDefault()

    const data = {
      email,
    }
    
    dispatch(requestPassword(data))
    console.log(message)
  }
  
  return (
    <div className="request-container">
        <form onSubmit={handleSubmit}>
            <input type="email" placeholder="Digite seu E-mail" required onChange={(e) => setEmail(e.target.value)}/>
            <button onClick={() => handleSubmit} className='btn_global'>Enviar</button>
            {/*!loading && <button onClick={() => handleSubmit} className='btn_global'>Enviar</button>*/}
            {/*loading && <button className='btn_global' disabled="disabled">Aguarde...</button>*/}
            {message && <Message type='success' msg={message}/>}
        </form>
    </div>
  )
}

export default RequestPassword
