import {api, requestConfig} from '../utils/config'

const requestPassword = async(email) => {
    const config = {
        method: "POST",
        body: JSON.stringify(email),
        headers: {
            "Content-Type": "application/json"
        }
    }
    try{
        const res = await fetch(api + "/users/request", config)
            .then((res) => res.json())
            .catch((err) => err)
        return res
    } catch(error){
        console.log(error)
    }
}

const setPassword = async(email, token, senha) => {
    const data = {
        email,
        token,
        senha
    }
    const config = {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
            "Content-Type": "application/json"
        }
    }
    try{
        const res = await fetch(api + "/users/", config)
            .then((res) => res.json())
            .catch((err) => err)
        return res
    } catch(error){
        console.log(error)
    }
}

const passwordService = {
    requestPassword,
}

export default passwordService
