import React, { useEffect, useState } from 'react'
import styles from './EditPedido.module.css'

//hooks
import { useAuth } from '../../hooks/useAuth'

//components
import MenuPerfil from '../../components/menu/MenuPerfil'


//
import { useDispatch, useSelector } from 'react-redux'
//redux
import { getPedidoById, updatePedido, resetMessage } from '../../slices/pedidoSlice'
import { useParams } from 'react-router-dom'

const EditPedido = () => {

  const {id} = useParams()
  const {auth} = useAuth()
  const dispatch = useDispatch()
  const {pedido, message, error} = useSelector((state) => state.pedido)

  useEffect(() => {
    dispatch(getPedidoById(id))
  }, [dispatch, id])

  const [title, setTitle] = useState(pedido.titulo)
  const [local, setLocal] = useState(pedido.local)
  const [tipos, setTipos] = useState(pedido.tipos)
  const [types, setTypes] = useState("")
  const [desc, setDesc] = useState(pedido.desc)

  console.log(title, local, tipos, desc)

  setTimeout(() => {
    dispatch(resetMessage(message))
  }, 2000)

  const addType = (type) => {
    //limpa seleção
    if(type===''){
      setTipos(type)
    }
    //não deixa duas vezes o mesmo tipo
    if(tipos.includes(type)){
      return
    }
    if(tipos === ""){
      setTipos(tipos + `${type}`)
      return
    }
    setTipos(tipos + `, ${type}`)
  }
  
  const handleSubmit = (e) => {
    e.preventDefault()
    console.log('entrou!!!')
  }
  return (
    <div className={styles.container}>
       {auth && (
          <>
            <MenuPerfil />
          </>
        )}
      <div className={styles.formEditPedido}>
        <h2>Edite aquilo que desejar</h2>
        <form onSubmit={handleSubmit}>
          
        </form>
      </div>
    </div>
  )
}

export default EditPedido
