import {api, requestConfig} from '../utils/config'

//get user details
const profile = async(data, token) => {
    const config = requestConfig("GET", data, token)

    try {
        const res = await fetch(api + "/users/profile", config)
        .then((res) => res.json())
        .catch((err) => err)
        
        return res
    } catch (error) {
        console.log(error)
    }
}

//update profile
const updateProfile = async(data, token) => {
    const config = requestConfig("PUT", data, token, true)
    try {
        const res = await fetch(api + "/users/update", config)
                    .then((res) => res.json())
                    .catch((err) => err)
        return res
    } catch (err) {
        console.log(err)
    }
}

//request password
const requestNewPassword = async(email) => {
    console.log(email)
    const config = requestConfig("POST", email)
    try {
        const res = await fetch(api + "/users/recupera", config)
            .then((res) => res.json())
            .catch((err) => err)
        return res
    } catch (error) {
        console.log(error)
    }
}

//set new password
const setNewPassword = async(data) => {
    const config = requestConfig("POST", data)
    try {
        const res = await fetch(api + "/users/reseta", config)
            .then((res) => res.json())
            .catch((err) => err)
        return res
    } catch (error) {
        console.log(error)
    }
}
const userService = {
    profile,
    updateProfile,
    requestNewPassword,
    setNewPassword
}

export default userService
